// Example POST method implementation:
async function getData(url = '', data = {}) {
    // Default options are marked with *
    const response = await fetch("https://www.recreation.gov/api/ticket/availability/facility/300015/monthlyAvailabilitySummaryView?year=2020&month=08&inventoryBucket=FIT", {
        "headers": {
          "accept": "application/json, text/plain, */*",
          "accept-language": "en-US,en;q=0.9",
          "authorization": "",
          "cache-control": "no-cache, no-store, must-revalidate",
          "pragma": "no-cache",
          "sec-fetch-dest": "empty",
          "sec-fetch-mode": "cors",
          "sec-fetch-site": "same-origin"
        },
        "referrer": "https://www.recreation.gov/ticket/facility/300015?q=Yosemite%20National%20Park%20Ticketed%20Entry",
        "referrerPolicy": "no-referrer-when-downgrade",
        "body": null,
        "method": "GET",
        "mode": "cors",
        "credentials": "include"
      });
    return response.json(); // parses JSON response into native JavaScript objects
  }

function run () {setInterval(()=>
getData('https://example.com/answer', { answer: 42 })
  .then(data => {

    spotsAvailable(data) // JSON data parsed by `data.json()` call
  }), 3000)}
